"use strict";var form=document.querySelector("form"),nameIpt=document.querySelector(".username"),passIpt=document.querySelector(".password");function clickHandler(e){(e=e||window.event).preventDefault();var t=nameIpt.value,a=passIpt.value;t&&a&&$.ajax({url:"/wtf",type:"get",dataType:"json",data:{username:t,password:a},success:function(e){0===e.code?alert("用户名或密码错误"):window.location.href="./index.html",console.log(e)}})}form.addEventListener("click",clickHandler);